import React, { useEffect, useState, useRef } from "react";
import PopupWithForm from "./popupWithForm";
import { userContext } from "../../contexts/CurrentUserContext";

function EditAvatarPopup(props) {
    const avatarRef = React.useRef()
    // function handleSubmit(e) {
    //     e.preventDefault();

    //     onUpdateAvatar({
    //       avatar: /* Значение инпута, полученное с помощью рефа */,
    //     });
    //   } 
    return (
    <PopupWithForm
        name='avatar'
        title='Сменить Аватар'
        closeAll={props.onClose}
        isOpen={props.isOpen}
        buttonText={'Сохранить'}
        className="menu-avatar__button menu__button menu__submit"
    >
        <input
            ref={avatarRef}
            id="avarat-link"
            type="url"
            name="link"
            required=""
            noValidate=""
            minLength={2}
            placeholder="Ссылка на картинку"
            className="form__input form-avatar__input"
        />
        <span
            id="avarat-link-error"
            className="error-span menu-avatar__error-span"
        />
    </PopupWithForm>
    )

}

export default EditAvatarPopup
