import React, { useEffect } from "react";
function PopupWithForm(props) {
    console.log(props);

    const [isActive, setIsActive] = useEffect(props.isOpen ?? false);



    return (
            <div className={`popup popup_type_${props.name} ${isActive ? 'popup_active' : ''}`}  >
                <div className="menu">
                    <button className="menu__close-icon close-icon" type="button"  onClick={props.closeAll}/>
                    <h2 className="menu__title">{props.title}</h2>
                    <form className="form" noValidate="" name={`${props.name}`}>
                        {props.children}
                    </form>
                </div>
            </div>
    )
}

export default PopupWithForm

